import React, { Component } from 'react';
import MenuItem from '../menu-item/menu-item.component';
import './directory.styles.scss';

class Directory extends Component {
    constructor(props) {
        super(props);
        this.state = {
            sections: [
                {
                    title: 'hats',
                    imageUrl: 'https://i.ibb.co/cvpntL1/hats.png',
                    id: 1,
                    linkUrl: 'shop/hats'
                },
                {
                    title: 'jackets',
                    imageUrl: 'https://i.ibb.co/px2tCc3/jackets.png',
                    id: 2,
                    linkUrl: 'shop/jackets'
                },
                {
                    title: 'sneakers',
                    imageUrl: 'https://i.ibb.co/0jqHpnp/sneakers.png',
                    id: 3,
                    linkUrl: 'shop/sneakers'
                },
                {
                    title: 'womens',
                    imageUrl: 'https://i.ibb.co/GCCdy8t/womens.png',
                    size: 'large',
                    id: 4,
                    linkUrl: 'shop/womens'
                },
                {
                    title: 'mens',
                    imageUrl: 'https://i.ibb.co/R70vBrQ/men.png',
                    size: 'large',
                    id: 5,
                    linkUrl: 'shop/mens'
                }
            ]
        }
    }
    render() {
        return (
            <div className="directory-menu">
                {
                    this.state.sections.map(({id, title, imageUrl, size}) => (
                        <MenuItem key={id} title={title.toUpperCase()} imageUrl= {imageUrl} size={size}/> 
                    ))
                }
            {/* <MenuItem title="HATS" subtitle="SHOP NOW" />
            <MenuItem title="JACKETS" subtitle="SHOP NOW" />
            <MenuItem title="SNEAKERS" subtitle="SHOP NOW" />
            <MenuItem title="WOMENS" subtitle="SHOP NOW" />
            <MenuItem title="MENS" subtitle="SHOP NOW" /> */}
        </div>
        );
    }
}

export default Directory;
