import { createSelector } from 'reselect';

const selectShop = state => state.shop;

const selectPageId = (_, props) => props.match.params.collectionType;

export const selectCollections = createSelector(
    [selectShop],
    shop => shop.collections
);


export const selectCollection = createSelector(
        [selectCollections, selectPageId],
        (collections, pageId) => {
            return collections.find(collection => collection.routeName === pageId)
        }
);
