import { createSelector } from 'reselect';

const selectShop = state => state.shop;

const selectPageId = (state, props) => props.match.params.pageId;

export const selectCollections = createSelector(
    [selectShop],
    shop => shop.collections
);

// Memoizing result of function, whenever this function gets called and receives collectionUrlParam,
// because mapStateToProps calls this function each time url changes so we can invoke this function more than once at the same time (also on refresh in same session)
export const selectCollection =
    createSelector(
        [selectCollections, selectPageId],
        (collections, pageId) => collections.filter(collection => collection.routeName === pageId)
        );
