import styled from 'styled-components';

export const PreviewCollectionComponent = styled.div`
    display: flex;
    flex-direction: column;
    margin-bottom: 30px;
`;

export const Title = styled.h1`
    font-size: 28px;
    margin-bottom: 25px;
`;

export const Preview = styled.div`
    display: flex;
    justify-content: space-between;
`;
